import {
  Accordion,
  AccordionSummary,
  AccordionDetails,
  Button,
  Stack,
  Box,
  TextField,
  FormControl,
  InputLabel,
  MenuItem,
  Select,
  useTheme,
  styled,
} from "@mui/material";
import { FC, useEffect, useState } from "react";
import ExpandMoreIcon from "@mui/icons-material/ExpandMore";
import {
  RecipeInfo,
  RecipeType,
  TimeType,
  YieldType,
  emptyRecipe,
} from "../../types/recipe";
import { useParams } from "react-router-dom";
import RecipeDisplay from "../../components/RecipeDisplay";
import RecipeList from "./forms/RecipeList";
import AddIcon from "@mui/icons-material/Add";
import RecipeYield from "./forms/RecipeYield";
import CloudUploadIcon from "@mui/icons-material/CloudUpload";

const RecipeForm: FC = () => {
  const [recipe, setRecipe] = useState<RecipeInfo>(emptyRecipe);
  const { type, category, recipeID } = useParams();
  const isEditMode = type !== undefined && recipeID !== undefined;
  const newYield: YieldType = { quantity: 0, piece: "people" };
  const newTime: TimeType = { time: 0, unit: "min" };
  const newRecipe: RecipeInfo = {
    id: "",
    title: "Recipe title",
    ingredients: [],
    preparation: [],
    yield: newYield,
    preparationTime: newTime,
    ovenTemperature: undefined,
    quote: undefined,
    type: RecipeType.Desserts,
    source: undefined,
    imgURL: "",
    category: undefined,
    language: undefined,
  };
  const theme = useTheme();

  const VisuallyHiddenInput = styled("input")({
    clip: "rect(0 0 0 0)",
    clipPath: "inset(50%)",
    height: 1,
    overflow: "hidden",
    position: "absolute",
    bottom: 0,
    left: 0,
    whiteSpace: "nowrap",
    width: 1,
  });

  useEffect(() => {
    if (isEditMode) {
      //fetch recipe
    } else {
      setRecipe(newRecipe);
    }
  }, []);

  const handleUpdate = (payload: any, action: string) => {
    if (recipe !== undefined) {
      switch (action) {
        case "title":
          setRecipe({ ...recipe, title: payload });
          break;
        case "type":
          setRecipe({ ...recipe, type: payload });
          break;
        case "yield":
          setRecipe({ ...recipe, yield: payload });
          break;
        case "time":
          setRecipe({ ...recipe, preparationTime: payload });
          break;
        case "category":
          setRecipe({ ...recipe, category: payload });
          break;
        case "oven":
          setRecipe({ ...recipe, ovenTemperature: payload });
          break;
        case "quote":
          setRecipe({ ...recipe, quote: payload });
          break;
        case "source":
          setRecipe({ ...recipe, source: payload });
          break;
        case "image":
          setRecipe({ ...recipe, imgURL: payload });
          break;
      }
    }
  };

  return (
    <Stack direction={"row"} spacing={2}>
      <Box width="50%">
        <Accordion defaultExpanded>
          <AccordionSummary
            expandIcon={<ExpandMoreIcon />}
            aria-controls="panel1-content"
            id="panel1-header"
          >
            Recipe Title
          </AccordionSummary>
          <AccordionDetails>
            <Stack direction={"row"} spacing={1}>
              <TextField
                fullWidth
                required
                id="outlined-required"
                label="Title"
                defaultValue="Recipe"
                size="small"
                onChange={(event) => handleUpdate(event.target.value, "title")}
              />
              <FormControl fullWidth sx={{ maxWidth: "30%" }}>
                <InputLabel id="demo-simple-select-label">
                  Recipe type
                </InputLabel>
                <Select
                  labelId="demo-simple-select-label"
                  id="demo-simple-select"
                  value={
                    recipe !== undefined ? recipe?.type : RecipeType.Desserts
                  }
                  label="Recipe Type"
                  size="small"
                  onChange={(event) => handleUpdate(event.target.value, "type")}
                >
                  <MenuItem value={RecipeType.Desserts}>Desserts</MenuItem>
                  <MenuItem value={RecipeType.Meals}>Meals</MenuItem>
                  <MenuItem value={RecipeType.Drinks}>Drinks</MenuItem>
                </Select>
              </FormControl>
            </Stack>
          </AccordionDetails>
        </Accordion>
        <Accordion>
          <AccordionSummary
            expandIcon={<ExpandMoreIcon />}
            aria-controls="panel2-content"
            id="panel2-header"
          >
            Ingredients
          </AccordionSummary>
          <AccordionDetails>
            <RecipeList
              list={recipe.ingredients || []}
              type="ingredients"
              setRecipe={(ingredients) => setRecipe({ ...recipe, ingredients })}
            />
          </AccordionDetails>
        </Accordion>
        <Accordion>
          <AccordionSummary
            expandIcon={<ExpandMoreIcon />}
            aria-controls="panel3-content"
            id="panel3-header"
          >
            Preparation
          </AccordionSummary>
          <AccordionDetails>
            <RecipeList
              list={recipe.preparation || []}
              type="preparation"
              setRecipe={(preparation) => setRecipe({ ...recipe, preparation })}
            />
          </AccordionDetails>
        </Accordion>
        <Accordion>
          <AccordionSummary
            expandIcon={<ExpandMoreIcon />}
            aria-controls="panel4-content"
            id="panel4-header"
          >
            Yield
          </AccordionSummary>
          <AccordionDetails>
            <RecipeYield
              recipeYield={recipe.yield}
              handleUpdate={(y) => handleUpdate(y, "yield")}
            />
          </AccordionDetails>
        </Accordion>
        <Accordion>
          <AccordionSummary
            expandIcon={<ExpandMoreIcon />}
            aria-controls="panel5-content"
            id="panel5-header"
          >
            Preparation Time
          </AccordionSummary>
          <AccordionDetails>
            <Stack direction="row" spacing={1}>
              <TextField
                id="outlined-required"
                value={recipe.preparationTime.time}
                size="small"
                onChange={(event) =>
                  handleUpdate(
                    { ...recipe.preparationTime, time: event.target.value },
                    "time"
                  )
                }
              />
              <FormControl fullWidth sx={{ maxWidth: "30%" }}>
                <InputLabel id="demo-simple-select-label">Unit</InputLabel>
                <Select
                  labelId="demo-simple-select-label"
                  id="demo-simple-select"
                  value={recipe.preparationTime.unit}
                  label="Unit"
                  size="small"
                  onChange={(event) =>
                    handleUpdate(
                      { ...recipe.preparationTime, unit: event.target.value },
                      "time"
                    )
                  }
                >
                  <MenuItem value={"h"}>Minute</MenuItem>
                  <MenuItem value={"min"}>Hour</MenuItem>
                </Select>
              </FormControl>
            </Stack>
          </AccordionDetails>
        </Accordion>
        <Accordion>
          <AccordionSummary
            expandIcon={<ExpandMoreIcon />}
            aria-controls="panel6-content"
            id="panel6-header"
          >
            Category
          </AccordionSummary>
          <AccordionDetails>
            <Button
              startIcon={<AddIcon />}
              variant="contained"
              size="small"
              sx={{
                width: "100%",
                backgroundColor: theme.palette.primary.light,
              }}
            >
              Add Category
            </Button>
          </AccordionDetails>
        </Accordion>
        <Accordion>
          <AccordionSummary
            expandIcon={<ExpandMoreIcon />}
            aria-controls="panel7-content"
            id="panel7-header"
          >
            Oven Temperature
          </AccordionSummary>
          <AccordionDetails>
            <Button
              startIcon={<AddIcon />}
              variant="contained"
              size="small"
              sx={{
                width: "100%",
                backgroundColor: theme.palette.primary.light,
              }}
            >
              Add Oven Temperature
            </Button>
          </AccordionDetails>
        </Accordion>
        <Accordion>
          <AccordionSummary
            expandIcon={<ExpandMoreIcon />}
            aria-controls="panel8-content"
            id="panel8-header"
          >
            Quote
          </AccordionSummary>
          <AccordionDetails>
            <Button
              startIcon={<AddIcon />}
              variant="contained"
              size="small"
              sx={{
                width: "100%",
                backgroundColor: theme.palette.primary.light,
              }}
            >
              Add Quote
            </Button>
          </AccordionDetails>
        </Accordion>
        <Accordion>
          <AccordionSummary
            expandIcon={<ExpandMoreIcon />}
            aria-controls="panel9-content"
            id="panel9-header"
          >
            Source
          </AccordionSummary>
          <AccordionDetails>
            <Button
              startIcon={<AddIcon />}
              variant="contained"
              size="small"
              sx={{
                width: "100%",
                backgroundColor: theme.palette.primary.light,
              }}
            >
              Add Source
            </Button>
          </AccordionDetails>
        </Accordion>
        <Accordion>
          <AccordionSummary
            expandIcon={<ExpandMoreIcon />}
            aria-controls="panel10-content"
            id="panel10-header"
          >
            Upload Image
          </AccordionSummary>
          <AccordionDetails>
            <Button
              fullWidth
              size="small"
              component="label"
              role={undefined}
              variant="contained"
              tabIndex={-1}
              startIcon={<CloudUploadIcon />}
            >
              Upload Image
              <VisuallyHiddenInput type="file" />
            </Button>
          </AccordionDetails>
        </Accordion>
      </Box>
      <Box width="50%">
        <RecipeDisplay recipe={recipe} preview={true} />
      </Box>
    </Stack>
  );
};

export default RecipeForm;
