import { FC } from 'react';
import { CategoryMetadata, Metadata, RecipeMetadata } from '../types/recipe';
import {
  Box,
  Card,
  CardActionArea,
  CardContent,
  CardMedia,
  ImageList,
  ImageListItem,
  Stack,
  Typography,
} from '@mui/material';
import { ROUTE_CATEGORY, ROUTE_RECIPE } from '../routes';
import { Link } from 'react-router-dom';
import dummy from '../assets/logo.png';

const RecipeThumbnail: FC<{ metadata: Metadata }> = ({ metadata }) => {
  const isCategory = metadata.recipes !== undefined;
  const route = isCategory
    ? ROUTE_CATEGORY(metadata.type, (metadata as CategoryMetadata).category)
    : ROUTE_RECIPE(metadata.type, (metadata as RecipeMetadata).id);
  return (
    <Stack
      width={'80%'}
      direction="column"
      justifyContent="center"
      alignItems="center"
    >
      <Link to={route}>
        <Card sx={{ height: '480px', marginBottom: 1 }}>
          <CardActionArea>
            <CardMedia
              component="img"
              height="480px"
              image={dummy}
              alt="green iguana"
            />
          </CardActionArea>
        </Card>
      </Link>
      <Link to={route}>
        <Typography
          variant="body1"
          color={'black'}
          sx={{ textDecoration: 'none' }}
          textAlign={'center'}
        >
          {metadata.title}
        </Typography>
      </Link>
    </Stack>
  );
};
