import { FC, useEffect, useState } from 'react';
import { RecipeInfo, RecipeType } from '../types/recipe';
import { api, api_recipe } from '../api/axiosConfig';
import { useParams } from 'react-router-dom';
import dummyLogo from '../assets/espresso_cookies.jpg';
import {
  Box,
  CircularProgress,
  Divider,
  Stack,
  Typography,
  useTheme,
} from '@mui/material';

const dummy: RecipeInfo = {
  id: '1',
  title: 'Cookies',
  ingredients: ['100g of butter', '200g of white sugar', '300g of chocolate'],
  preparation: [
    'Melt the butter',
    'Mix everything',
    'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.',
  ],
  yield: { quantity: 24, piece: 'pieces' },
  preparationTime: { time: 60, unit: 'min' },
  ovenTemperature: 200,
  quote: {
    content: 'Hi, the best cookies post hangover',
    author: 'me, and chief or captain',
  },
  source: { content: "Some child's book", author: 'me' },
  imgURL: dummyLogo,
  type: RecipeType.Desserts,
  category: 'More cookies',
  language: 'en',
};

const Recipe: FC = () => {
  const { type, id, category } = useParams();
  const theme = useTheme();
  const [recipe, setRecipe] = useState<RecipeInfo>();

  useEffect(() => {
    if (type !== undefined) {
      api_recipe(type as RecipeType, category)
        .get(`/${id}`)
        .then((result) => setRecipe(result.data))
        .catch(() => {
          setRecipe(dummy);
        });
    }
    // .catch((reason) => console.error(reason));
  }, []);
  // className="flex-none object-cover w-11/12 max-h-[600px] max-w-[450px]"

  return (
    <Box width={'80%'} marginTop={4} marginBottom={4}>
      {recipe !== undefined ? (
        <>
          <Typography
            textAlign={'center'}
            variant="h2"
            color={theme.palette.primary.main}
            marginBottom={6}
          >
            {recipe.title}
          </Typography>
          <Stack direction={'row'} width={'100%'} spacing={4} marginBottom={3}>
            <img
              src={recipe.imgURL}
              alt={recipe.title}
              style={{
                objectFit: 'cover',
                width: '50%',
                maxWidth: '450px',
                maxHeight: '600px',
              }}
            />
            {/* <Divider orientation="vertical" flexItem /> */}
            {recipe.quote !== undefined && (
              <Stack direction={'column'}>
                <Box sx={{ width: 30, height: 30 }}>
                  <svg
                    color={theme.palette.secondary.main}
                    aria-hidden="true"
                    viewBox="0 0 24 27"
                    fill="none"
                    xmlns="http://www.w3.org/2000/svg"
                  >
                    <path
                      d="M14.017 18L14.017 10.609C14.017 4.905 17.748 1.039 23 0L23.995 2.151C21.563 3.068 20 5.789 20 8H24V18H14.017ZM0 18V10.609C0 4.905 3.748 1.038 9 0L9.996 2.151C7.563 3.068 6 5.789 6 8H9.983L9.983 18L0 18Z"
                      fill="currentColor"
                    />
                  </svg>
                </Box>
                <Typography fontStyle={'italic'} variant="h6" marginTop={1}>
                  "{recipe.quote.content}"
                </Typography>
                <Typography variant="h6" marginLeft={4}>
                  — {recipe.quote.author}
                </Typography>
              </Stack>
            )}
          </Stack>
          <Divider />
          <Stack direction={'row'} marginTop={2} spacing={2}>
            <Typography fontWeight={'bold'}>YIELD</Typography>
            <Typography>
              {recipe.yield.quantity} {recipe.yield.piece}
            </Typography>
          </Stack>
          <Stack direction={'row'} spacing={2}>
            <Typography fontWeight={'bold'}>TIME</Typography>
            <Typography>
              {recipe.preparationTime.time} {recipe.preparationTime.unit}
            </Typography>
          </Stack>
          {recipe.ovenTemperature !== undefined && (
            <Stack direction={'row'} spacing={2}>
              <Typography fontWeight={'bold'}>TIME</Typography>
              <Typography>
                {recipe.preparationTime.time} {recipe.preparationTime.unit}
              </Typography>
            </Stack>
          )}
          <Divider sx={{ marginTop: 2 }} />
          <Typography fontWeight={'bold'} marginY={2}>
            INGREDIENTS
          </Typography>
          <Box marginLeft={2} marginBottom={2}>
            {recipe.ingredients.map((ingredient) => (
              <Typography>- {ingredient}</Typography>
            ))}
          </Box>
          <Divider />
          <Typography fontWeight={'bold'} marginY={2}>
            PREPARATION
          </Typography>
          <Box marginBottom={2}>
            {recipe.preparation.map((step, i) => (
              <Stack marginBottom={2}>
                <Typography fontWeight={'bold'}>Step {i + 1}</Typography>
                <Typography marginLeft={2}>{step}</Typography>
              </Stack>
            ))}
          </Box>
          <Divider />
          {recipe.source !== undefined && (
            <Typography variant="body2" marginTop={2}>
              Source: {recipe.source.content} — {recipe.source.author}
            </Typography>
          )}
        </>
      ) : (
        <CircularProgress />
      )}
    </Box>
  );
};

export default Recipe;
