import {
  Accordion,
  AccordionSummary,
  AccordionDetails,
  AccordionActions,
  Button,
  Stack,
  Box,
  TextField,
  FormControl,
  InputLabel,
  MenuItem,
  Select,
  useTheme,
  Typography,
  IconButton,
} from "@mui/material";
import ExpandMoreIcon from "@mui/icons-material/ExpandMore";
import { FC, useEffect, useState } from "react";
import {
  RecipeInfo,
  RecipeType,
  TimeType,
  YieldType,
  emptyRecipe,
} from "../../types/recipe";
import { useParams } from "react-router-dom";
import RecipeDisplay from "../../components/RecipeDisplay";
import AddIcon from "@mui/icons-material/Add";
import RemoveIcon from "@mui/icons-material/Remove";

const RecipeForm: FC = () => {
  const [recipe, setRecipe] = useState<RecipeInfo>(emptyRecipe);
  const { type, category, recipeID } = useParams();
  const isEditMode = type !== undefined && recipeID !== undefined;
  const newYield: YieldType = { quantity: 0, piece: "people" };
  const newTime: TimeType = { time: 0, unit: "min" };
  const newRecipe: RecipeInfo = {
    id: "",
    title: "",
    ingredients: [],
    preparation: [],
    yield: newYield,
    preparationTime: newTime,
    ovenTemperature: undefined,
    quote: undefined,
    type: RecipeType.Desserts,
    source: undefined,
    imgURL: "",
    category: undefined,
    language: undefined,
  };
  const theme = useTheme();

  useEffect(() => {
    if (isEditMode) {
      //fetch recipe
    } else {
      setRecipe(newRecipe);
    }
  }, []);

  const handleUpdate = (payload: any, action: string) => {
    if (recipe !== undefined) {
      switch (action) {
        case "title":
          setRecipe({ ...recipe, title: payload });
          break;
        case "type":
          setRecipe({ ...recipe, type: payload });
          break;
        case "yield":
          setRecipe({ ...recipe, yield: payload });
          break;
        case "time":
          setRecipe({ ...recipe, preparationTime: payload });
          break;
        case "category":
          setRecipe({ ...recipe, category: payload });
          break;
        case "oven":
          setRecipe({ ...recipe, ovenTemperature: payload });
          break;
        case "quote":
          setRecipe({ ...recipe, quote: payload });
          break;
        case "source":
          setRecipe({ ...recipe, source: payload });
          break;
        case "image":
          setRecipe({ ...recipe, imgURL: payload });
          break;
      }
    }
  };

  const handleUpdateIngredients = (
    payload: string,
    index: number,
    action: "add" | "remove" | "update"
  ) => {
    if (recipe !== undefined) {
      setRecipe({
        ...recipe,
        ingredients: [
          ...getUpdatedList(recipe?.ingredients || [], action, index, payload),
        ],
      });
    }
  };

  const handleUpdatePreparation = (
    payload: string,
    index: number,
    action: "add" | "remove" | "update"
  ) => {
    if (recipe !== undefined) {
      var preparation = getUpdatedList(
        recipe?.preparation || [],
        action,
        index,
        payload
      );
      setRecipe({ ...recipe, preparation });
    }
  };

  const getUpdatedList = (
    list: string[],
    action: "add" | "remove" | "update",
    index: number,
    payload: string
  ) => {
    console.log(action + " " + index);
    switch (action) {
      case "add":
        var newList = [...list];
        if (index !== 0 && index !== list.length) {
          newList.splice(index, 0, "");
        } else {
          newList.push("")
        } 
        return newList;
      case "remove":
        var newList = [...list];
        newList.splice(index, 1);
        return newList;
      case "update":
        var newList = [...list];
        newList[index] = payload;
        return newList;
    }
  };

  return (
    <Stack direction={"row"} spacing={2}>
      <Box width="50%">
        <Accordion defaultExpanded>
          <AccordionSummary
            expandIcon={<ExpandMoreIcon />}
            aria-controls="panel1-content"
            id="panel1-header"
          >
            Recipe Title
          </AccordionSummary>
          <AccordionDetails>
            <Stack direction={"row"} spacing={1}>
              <TextField
                fullWidth
                required
                id="outlined-required"
                label="Title"
                defaultValue=""
                size="small"
                onChange={(target) =>
                  handleUpdate(target.target.value, "title")
                }
              />
              <FormControl fullWidth sx={{ maxWidth: "30%" }}>
                <InputLabel id="demo-simple-select-label">
                  Recipe type
                </InputLabel>
                <Select
                  labelId="demo-simple-select-label"
                  id="demo-simple-select"
                  value={
