import { FC } from "react";
import { YieldType } from "../../../types/recipe";
import { Stack, Typography, Paper, IconButton, InputBase, FormControl, FormControlLabel, FormLabel, Radio, RadioGroup } from "@mui/material";
import AddIcon from "@mui/icons-material/Add";
import RemoveIcon from "@mui/icons-material/Remove";

type RecipeYieldProps = {
  recipeYield: YieldType;
  handleUpdate: (y: YieldType) => void;
};

const RecipeYield: FC<RecipeYieldProps> = ({ recipeYield, handleUpdate }) => {

    const handleChange = (event: React.ChangeEvent<HTMLInputElement>) => {
        setSelectedValue(event.target.value);
      };

      const controlProps = (item: string) => ({
        checked: selectedValue === item,
        onChange: handleChange,
        value: item,
        name: 'size-radio-button-demo',
        inputProps: { 'aria-label': item },
      });
  return (
    <Stack direction={"column"}>
      <Stack
        direction={"row"}
        spacing={2}
        alignItems={"center"}
        marginBottom={2}
      >
        <Typography>Quantity:</Typography>
        <Paper
          component="form"
          sx={{
            p: "2px 4px",
            display: "flex",
            alignItems: "center",
            width: 120,
          }}
        >
          <IconButton
            sx={{ p: "10px" }}
            size="small"
            onClick={() =>
              handleUpdate({
                ...recipeYield,
                quantity: recipeYield.quantity + 1,
              })
            }
          >
            <AddIcon fontSize="inherit" />
          </IconButton>
          <InputBase
            sx={{ ml: 1, flex: 1 }}
            placeholder=""
            value={recipeYield.quantity}
            onChange={(target) =>
              handleUpdate({
                ...recipeYield,
                quantity: Number.parseInt(target.target.value),
              })
            }
          />
          <IconButton
            size="small"
            type="button"
            sx={{ p: "10px" }}
            onClick={() =>
              handleUpdate({
                ...recipeYield,
                quantity:
                  recipeYield.quantity > 0 ? recipeYield.quantity - 1 : 0,
              })
            }
          >
            <RemoveIcon fontSize="inherit" />
          </IconButton>
        </Paper>
      </Stack>
      <Stack direction={"row"} spacing={2} alignItems={"center"}>
        <Typography>Serving:</Typography>
        <FormControl>
          <FormLabel id="demo-row-radio-buttons-group-label">Gender</FormLabel>
          <RadioGroup
            row
            aria-labelledby="demo-row-radio-buttons-group-label"
            name="row-radio-buttons-group"
          >
            <FormControlLabel
              value="female"
              control={<Radio size="small"/>}
              label="Female"
            />
            <FormControlLabel sx={{fontSize:10}} value="male" control={<Radio size="small"/>} label="Male" />
            <FormControlLabel value="other" control={<Radio size="small"/>} label="Other" />
            <FormControlLabel
              value="disabled"
              disabled
              control={<Radio />}
              label="other"
            />
          </RadioGroup>
        </FormControl>
      </Stack>
    </Stack>
  );
};

export default RecipeYield;
