import {
  Accordion,
  AccordionSummary,
  AccordionDetails,
  AccordionActions,
  Button,
  Stack,
  Box,
  TextField,
  FormControl,
  InputLabel,
  MenuItem,
  Select,
  useTheme,
  Typography,
} from "@mui/material";
import ExpandMoreIcon from "@mui/icons-material/ExpandMore";
import { FC, useEffect, useState } from "react";
import {
  RecipeInfo,
  RecipeType,
  TimeType,
  YieldType,
} from "../../types/recipe";
import { useParams } from "react-router-dom";
import RecipeDisplay from "../../components/RecipeDisplay";
import AddIcon from "@mui/icons-material/Add";

const RecipeForm: FC = () => {
  const [recipe, setRecipe] = useState<RecipeInfo>();
  const { type, category, recipeID } = useParams();
  const isEditMode = type !== undefined && recipeID !== undefined;
  const newYield: YieldType = { quantity: 0, piece: "people" };
  const newTime: TimeType = { time: 0, unit: "min" };
  const newRecipe: RecipeInfo = {
    id: "",
    title: "",
    ingredients: [],
    preparation: [],
    yield: newYield,
    preparationTime: newTime,
    ovenTemperature: undefined,
    quote: undefined,
    type: RecipeType.Desserts,
    source: undefined,
    imgURL: "",
    category: undefined,
    language: undefined,
  };
  const theme = useTheme();

  useEffect(() => {
    if (isEditMode) {
      //fetch recipe
    } else {
      setRecipe(newRecipe);
    }
  }, []);

  const handleUpdate = (payload: any, action: string) => {
    switch (action) {
      case "title":
        setRecipe({ ...recipe, title: payload } as RecipeInfo);
        break;
      case "type":
        setRecipe({ ...recipe, type: payload } as RecipeInfo);
        break;
      case "ingredients":
        setRecipe({ ...recipe, ingredients: payload } as RecipeInfo);
        break;
      case "preparation":
        setRecipe({ ...recipe, preparation: payload } as RecipeInfo);
        break;
      case "yield":
        setRecipe({ ...recipe, yield: payload } as RecipeInfo);
        break;
      case "time":
        setRecipe({ ...recipe, time: payload } as RecipeInfo);
        break;
      case "category":
        setRecipe({ ...recipe, category: payload } as RecipeInfo);
        break;
      case "oven":
        setRecipe({ ...recipe, ovenTemperature: payload } as RecipeInfo);
        break;
      case "quote":
        setRecipe({ ...recipe, quote: payload } as RecipeInfo);
        break;
      case "source":
        setRecipe({ ...recipe, source: payload } as RecipeInfo);
        break;
      case "image":
        setRecipe({ ...recipe, imgURL: payload } as RecipeInfo);
        break;
    }
  };

  const handleUpdateIngredient = (action: "add" | "remove", index?: number) => {
    switch(action) {
      case "add":
        if (recipe !== undefined) {
          var ingredients = [...recipe.ingredients, ""]
          setRecipe({...recipe, ingredients})
        }
        break;
      case "remove":
        if (index !== undefined && recipe !== undefined) {
          var ingredients = recipe?.ingredients
          setRecipe({...recipe, ingredients: [...ingredients.slice(0, index), ...ingredients.slice(index+1, ingredients.length)]})
        }
        break;
    }
  }

  return (
    <Stack direction={"row"} spacing={2}>
      <Box width="50%">
        <Accordion defaultExpanded>
          <AccordionSummary
            expandIcon={<ExpandMoreIcon />}
            aria-controls="panel1-content"
            id="panel1-header"
          >
            Recipe Title
          </AccordionSummary>
          <AccordionDetails>
            <Stack direction={"row"} spacing={1}>
              <TextField
                fullWidth
                required
                id="outlined-required"
                label="Title"
                defaultValue=""
                size="small"
                onChange={(target) =>
                  handleUpdate(target.target.value, "title")
                }
              />
              <FormControl fullWidth sx={{ maxWidth: "30%" }}>
                <InputLabel id="demo-simple-select-label">
                  Recipe type
                </InputLabel>
                <Select
                  labelId="demo-simple-select-label"
                  id="demo-simple-select"
                  value={
                    recipe !== undefined ? recipe?.type : RecipeType.Desserts
                  }
                  label="Recipe Type"
                  size="small"
                  onChange={(target) =>
                    handleUpdate(target.target.value, "type")
                  }
                >
                  <MenuItem value={RecipeType.Desserts}>Desserts</MenuItem>
                  <MenuItem value={RecipeType.Meals}>Meals</MenuItem>
                  <MenuItem value={RecipeType.Drinks}>Drinks</MenuItem>
                </Select>
              </FormControl>
            </Stack>
          </AccordionDetails>
        </Accordion>
        <Accordion>
          <AccordionSummary
            expandIcon={<ExpandMoreIcon />}
            aria-controls="panel2-content"
            id="panel2-header"
          >
            Ingredients
          </AccordionSummary>
          <AccordionDetails>
            {recipe?.ingredients.map((ingredient, i) => (
              <Stack key={i}>
                  <TextField
                fullWidth
                required
                id="outlined-required"
                label="Title"
                defaultValue=""
                size="small"
                onChange={(target) =>
                  handleUpdate(target.target.value, "title")
                }
              />

              </Stack>
            ))}
            <Button
              onClick={() => handleUpdateIngredient("add")}
              startIcon={<AddIcon />}
              variant="contained"
              size="small"
              sx={{ backgroundColor: theme.palette.primary.light }}
            >
              Add Ingredient
            </Button>
          </AccordionDetails>
        </Accordion>
        <Accordion>
          <AccordionSummary
            expandIcon={<ExpandMoreIcon />}
            aria-controls="panel3-content"
            id="panel3-header"
          >
            Preparation
          </AccordionSummary>
          <AccordionDetails>
            Lorem ipsum dolor sit amet, consectetur adipiscing elit. Suspendisse
            malesuada lacus ex, sit amet blandit leo lobortis eget.
          </AccordionDetails>
        </Accordion>
        <Accordion>
          <AccordionSummary
            expandIcon={<ExpandMoreIcon />}
            aria-controls="panel4-content"
            id="panel4-header"
