import {
  Accordion,
  AccordionSummary,
  AccordionDetails,
  AccordionActions,
  Button,
  Stack,
  Box,
  TextField,
  FormControl,
  InputLabel,
  MenuItem,
  Select,
} from "@mui/material";
import ExpandMoreIcon from "@mui/icons-material/ExpandMore";
import { FC, useEffect, useState } from "react";
import {
  RecipeInfo,
  RecipeType,
  TimeType,
  YieldType,
} from "../../types/recipe";
import { useParams } from "react-router-dom";
import RecipeDisplay from "../../components/RecipeDisplay";

const RecipeForm: FC = () => {
  const [recipe, setRecipe] = useState<RecipeInfo>();
  const { type, category, recipeID } = useParams();
  const isEditMode = type !== undefined && recipeID !== undefined;
  const newYield: YieldType = { quantity: 0, piece: "people" };
  const newTime: TimeType = { time: 0, unit: "min" };
  const newRecipe: RecipeInfo = {
    id: "",
    title: "",
    ingredients: [],
    preparation: [],
    yield: newYield,
    preparationTime: newTime,
    ovenTemperature: undefined,
    quote: undefined,
    type: RecipeType.Desserts,
    source: undefined,
    imgURL: "",
    category: undefined,
    language: undefined,
  };

  useEffect(() => {
    if (isEditMode) {
      //fetch recipe
    } else {
      setRecipe(newRecipe);
    }
  }, []);

  const handleUpdate = (payload: any, action: string) => {
    switch (action) {
      case "title":
        setRecipe({ ...recipe, title: payload } as RecipeInfo);
        break;
      case "type":
        setRecipe({ ...recipe, type: payload } as RecipeInfo);
        break;
      case "ingredients":
        setRecipe({ ...recipe, ingredients: payload } as RecipeInfo);
        break;
      case "preparation":
        setRecipe({ ...recipe, preparation: payload } as RecipeInfo);
        break;
      case "yield":
        setRecipe({ ...recipe, yield: payload } as RecipeInfo);
        break;
      case "time":
        setRecipe({ ...recipe, time: payload } as RecipeInfo);
        break;
      case "category":
        setRecipe({ ...recipe, category: payload } as RecipeInfo);
        break;
      case "oven":
        setRecipe({ ...recipe, ovenTemperature: payload } as RecipeInfo);
        break;
      case "quote":
        setRecipe({ ...recipe, quote: payload } as RecipeInfo);
        break;
      case "source":
        setRecipe({ ...recipe, source: payload } as RecipeInfo);
        break;
      case "image":
        setRecipe({ ...recipe, imgURL: payload } as RecipeInfo);
        break;
    }
  };

  return (
    <Stack direction={"row"} spacing={2}>
      <Box width="50%">
        <Accordion defaultExpanded>
          <AccordionSummary
            expandIcon={<ExpandMoreIcon />}
            aria-controls="panel1-content"
            id="panel1-header"
          >
            Recipe Title
          </AccordionSummary>
          <AccordionDetails>
            <Stack direction={"row"} spacing={1}>
              <TextField
                fullWidth
                required
                id="outlined-required"
                label="Title"
                defaultValue=""
                size="small"
              />
              <FormControl>
                <InputLabel id="demo-simple-select-label">Recipe type</InputLabel>
                <Select
                  labelId="demo-simple-select-label"
                  id="demo-simple-select"
                  value={RecipeType.Desserts}
                  label="Recipe Type"
                  size="small"
                  onChange={(value) => handleUpdate(value, "type")}
                >
                  <MenuItem value={RecipeType.Desserts}>Desserts</MenuItem>
                  <MenuItem value={RecipeType.Meals}>Meals</MenuItem>
                  <MenuItem value={RecipeType.Drinks}>Drinks</MenuItem>
                </Select>
              </FormControl>
            </Stack>
          </AccordionDetails>
        </Accordion>
        <Accordion>
          <AccordionSummary
            expandIcon={<ExpandMoreIcon />}
            aria-controls="panel2-content"
            id="panel2-header"
          >
            Ingredients
          </AccordionSummary>
          <AccordionDetails>
            Lorem ipsum dolor sit amet, consectetur adipiscing elit. Suspendisse
            malesuada lacus ex, sit amet blandit leo lobortis eget.
          </AccordionDetails>
        </Accordion>
        <Accordion defaultExpanded>
          <AccordionSummary
            expandIcon={<ExpandMoreIcon />}
            aria-controls="panel3-content"
            id="panel3-header"
          >
            Preparation
          </AccordionSummary>
          <AccordionDetails>
            Lorem ipsum dolor sit amet, consectetur adipiscing elit. Suspendisse
            malesuada lacus ex, sit amet blandit leo lobortis eget.
          </AccordionDetails>
          <AccordionActions>
            <Button>Cancel</Button>
            <Button>Agree</Button>
          </AccordionActions>
        </Accordion>
      </Box>
      <Box width="50%">
        <RecipeDisplay recipe={recipe} preview={true} />
      </Box>
    </Stack>
  );
};

export default RecipeForm;
