import { FC } from 'react';
import { CategoryMetadata, Metadata, RecipeMetadata } from '../types/recipe';
import { Box, Typography } from '@mui/material';
import { ROUTE_CATEGORY, ROUTE_RECIPE } from '../routes';
import { Link } from 'react-router-dom';

const RecipeThumbnail: FC<{ metadata: Metadata }> = ({ metadata }) => {
  const isCategoryMetadata =
    metadata.category !== undefined && metadata.recipes !== undefined;
  const instance = isCategoryMetadata
    ? (metadata as CategoryMetadata)
    : (metadata as RecipeMetadata);
  return (
    <Link
      to={
        isCategoryMetadata
          ? ROUTE_CATEGORY(metadata.type, metadata.category)
          : ROUTE_RECIPE(metadata.type, metadata.id, metadata?.category)
      }
    >
      <Box height={'480px'}>
        <img src={metadata.imgURL} alt="" className="object-cover h-[480px]" />
      </Box>
      <Typography textAlign={'center'}>{metadata.title}</Typography>
    </Link>
  );
};

