import { FC } from 'react';
import { CategoryMetadata, Metadata, RecipeMetadata } from '../types/recipe';
import { Box, Card, ImageList, ImageListItem, Typography } from '@mui/material';
import { ROUTE_CATEGORY, ROUTE_RECIPE } from '../routes';
import { Link } from 'react-router-dom';
import dummy from '../assets/logo.png';

const RecipeThumbnail: FC<{ metadata: Metadata }> = ({ metadata }) => {
  const isCategory = metadata.recipes !== undefined;
  return (
    <Link
      to={
        isCategory
          ? ROUTE_CATEGORY(
              metadata.type,
              (metadata as CategoryMetadata).category
            )
          : ROUTE_RECIPE(metadata.type, (metadata as RecipeMetadata).id)
      }
    >
      <Box width={'100%'} display="flex" justifyContent={'center'}>
        <Card sx={{ maxHeight: '480px', width: '80%', marginBottom: 1 }}>
          {isCategory ? (
            <ImageList
              sx={{ width: '100%', height: '480px' }}
              variant="quilted"
              cols={2}
              // rowHeight={"240px"}
            >
              {[].map((item) => (
                <ImageListItem
                // key={item.img}
                // cols={item.cols || 1}
                // rows={item.rows || 1}
                >
                  {/* <img
                       {...srcset(item.img, 121, item.rows, item.cols)}
                       alt={item.title}
                       loading="lazy"
                     /> */}
                </ImageListItem>
              ))}
            </ImageList>
          ) : (
            <img
              // srcSet={`${metadata.imgURL}?h=480&fit=object-cover&auto=format`}
              src={dummy}
              alt={metadata.title}
              loading="lazy"
              height="480px"
            />
          )}
        </Card>
      </Box>
      <Typography textAlign={'center'}>{metadata.title}</Typography>
    </Link>
  );
};

