import {
  Accordion,
  AccordionSummary,
  AccordionDetails,
  AccordionActions,
  Button,
  Stack,
  Box,
  TextField,
  FormControl,
  InputLabel,
  MenuItem,
  Select,
  useTheme,
  Typography,
} from "@mui/material";
import ExpandMoreIcon from "@mui/icons-material/ExpandMore";
import { FC, useEffect, useState } from "react";
import {
  RecipeInfo,
  RecipeType,
  TimeType,
  YieldType,
} from "../../types/recipe";
import { useParams } from "react-router-dom";
import RecipeDisplay from "../../components/RecipeDisplay";
import AddIcon from "@mui/icons-material/Add";

const RecipeForm: FC = () => {
  const [recipe, setRecipe] = useState<RecipeInfo>();
  const { type, category, recipeID } = useParams();
  const isEditMode = type !== undefined && recipeID !== undefined;
  const newYield: YieldType = { quantity: 0, piece: "people" };
  const newTime: TimeType = { time: 0, unit: "min" };
  const newRecipe: RecipeInfo = {
    id: "",
    title: "",
    ingredients: [],
    preparation: [],
    yield: newYield,
    preparationTime: newTime,
    ovenTemperature: undefined,
    quote: undefined,
    type: RecipeType.Desserts,
    source: undefined,
    imgURL: "",
    category: undefined,
    language: undefined,
  };
  const theme = useTheme();

  useEffect(() => {
    if (isEditMode) {
      //fetch recipe
    } else {
      setRecipe(newRecipe);
    }
  }, []);

  const handleUpdate = (payload: any, action: string) => {
    if (recipe !== undefined) {
      switch (action) {
        case "title":
          setRecipe({ ...recipe, title: payload });
          break;
        case "type":
          setRecipe({ ...recipe, type: payload });
          break;
        case "yield":
          setRecipe({ ...recipe, yield: payload });
          break;
        case "time":
          setRecipe({ ...recipe, preparationTime: payload });
          break;
        case "category":
          setRecipe({ ...recipe, category: payload });
          break;
        case "oven":
          setRecipe({ ...recipe, ovenTemperature: payload });
          break;
        case "quote":
          setRecipe({ ...recipe, quote: payload });
          break;
        case "source":
          setRecipe({ ...recipe, source: payload });
          break;
        case "image":
          setRecipe({ ...recipe, imgURL: payload });
          break;
      }
    }
  };

  const handleUpdateList = (
    payload: string,
    index: number,
    type: "ingredients" | "preparation",
    action: "add" | "remove" | "update"
  ) => {
    if (recipe !== undefined) {
      switch (type) {
        case "ingredients":
          var ingredients =
          setRecipe({ ...recipe, ingredients });
          break;
        case "preparation":
          var preparation = 
          setRecipe({ ...recipe, preparation });
          break;
      }
    }
  };

  const handleUpdateIngredient = (list: string[], payload: string, action: "add" | "remove" | "update", index: number) => {

    switch (action) {
      case "add":
          return  [
            ...list.slice(0, index),
            payload,
            ...list.slice(index),
          ];


      case "remove":
        if (index !== undefined && recipe !== undefined) {
          var ingredients = recipe?.ingredients;
          setRecipe({
            ...recipe,
            ingredients: [
              ...ingredients.slice(0, index),
              ...ingredients.slice(index + 1, ingredients.length),
            ],
          });
        }
        break;
    }
  };

  return (
    <Stack direction={"row"} spacing={2}>
      <Box width="50%">
        <Accordion defaultExpanded>
          <AccordionSummary
            expandIcon={<ExpandMoreIcon />}
            aria-controls="panel1-content"
            id="panel1-header"
          >
            Recipe Title
          </AccordionSummary>
          <AccordionDetails>
            <Stack direction={"row"} spacing={1}>
              <TextField
                fullWidth
                required
                id="outlined-required"
                label="Title"
                defaultValue=""
                size="small"
                onChange={(target) =>
                  handleUpdate(target.target.value, "title")
                }
              />
              <FormControl fullWidth sx={{ maxWidth: "30%" }}>
                <InputLabel id="demo-simple-select-label">
                  Recipe type
                </InputLabel>
                <Select
                  labelId="demo-simple-select-label"
                  id="demo-simple-select"
                  value={
                    recipe !== undefined ? recipe?.type : RecipeType.Desserts
                  }
                  label="Recipe Type"
                  size="small"
                  onChange={(target) =>
                    handleUpdate(target.target.value, "type")
                  }
                >
                  <MenuItem value={RecipeType.Desserts}>Desserts</MenuItem>
                  <MenuItem value={RecipeType.Meals}>Meals</MenuItem>
                  <MenuItem value={RecipeType.Drinks}>Drinks</MenuItem>
                </Select>
              </FormControl>
            </Stack>
          </AccordionDetails>
        </Accordion>
        <Accordion>
          <AccordionSummary
            expandIcon={<ExpandMoreIcon />}
            aria-controls="panel2-content"
            id="panel2-header"
          >
            Ingredients
          </AccordionSummary>
          <AccordionDetails>
            {recipe?.ingredients.map((ingredient, i) => (
              <Stack key={i} direction={"row"}>
                <Typography>-</Typography>
                <TextField
                  fullWidth
                  required
                  id="outlined-required"
                  label="Title"
                  defaultValue={ingredient}
                  size="small"
                  onChange={(target) =>
                    handleUpdateList(target.target.value, i, "ingredients")
                  }
                />
              </Stack>
            ))}
            <Button
              onClick={() => handleUpdateIngredient("add", recipe?.ingredients.length)}
              startIcon={<AddIcon />}
