import { Stack, TextField, IconButton, Button, Box } from "@mui/material";
import { FC } from "react";
import { theme } from "../../styles/theme";
import AddIcon from "@mui/icons-material/Add";
import RemoveIcon from "@mui/icons-material/Remove";

type RecipeListProps = {
  list: string[];
  type: "ingredients" | "preparation";
  setRecipe: (list: string[]) => void;
};
const RecipeList: FC<RecipeListProps> = ({ list, type, setRecipe }) => {
  const handleUpdate = (
    payload: string,
    index: number,
    action: "add" | "remove" | "update"
  ) => {
    const ingredients = getUpdatedList(list, action, index, payload);
    setRecipe(ingredients);
  };

  const getUpdatedList = (
    list: string[],
    action: "add" | "remove" | "update",
    index: number,
    payload: string
  ) => {
    const newList = [...list];

    switch (action) {
      case "add":
        if (index !== 0 && index !== list.length) {
          newList.splice(index, 0, "");
        } else {
          newList.push("");
        }
        return newList;
      case "remove":
        newList.splice(index, 1);
        return newList;
      case "update":
        newList[index] = payload;
        return newList;
    }
  };

  return (
    <Stack direction={"column"} spacing={2}>
      {list.map((ingredient, i) => (
        <Stack key={i} direction={"row"}>
          <TextField
            fullWidth
            id="outlined-required"
            label={type === "ingredients" ? "Ingredient" : "Step"}
            value={ingredient}
            size="small"
            multiline={type === "preparation"}
            onChange={(target) =>
              handleUpdate(target.target.value, i, "update")
            }
          />
          <Box>
            <IconButton onClick={() => handleUpdate("", i + 1, "add")}>
              <AddIcon />
            </IconButton>
            <IconButton onClick={() => handleUpdate("", i, "remove")}>
              <RemoveIcon />
            </IconButton>
          </Box>
        </Stack>
      ))}
      <Button
        onClick={() => handleUpdate("", list.length, "add")}
        startIcon={<AddIcon />}
        variant="contained"
        size="small"
        sx={{ backgroundColor: theme.palette.primary.light }}
      >
        Add {type === "ingredients" ? "Ingredient" : "Preparation Step"}
      </Button>
    </Stack>
  );
};

export default RecipeList;
