import { FC, useEffect, useState } from 'react';
import { RecipeInfo, RecipeType } from '../types/recipe';
import { api, api_recipe } from '../api/axiosConfig';
import { useParams } from 'react-router-dom';
import dummyLogo from '../assets/espresso_cookies.jpg';
import {
  Box,
  CircularProgress,
  Divider,
  Stack,
  Typography,
  useTheme,
} from '@mui/material';

const dummy: RecipeInfo = {
  id: '1',
  title: 'Cookies',
  ingredients: ['100g of butter', '200g of white sugar', '300g of chocolate'],
  preparation: [
    'Melt the butter',
    'Mix everything',
    'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.',
  ],
  yield: { quantity: 24, piece: 'pieces' },
  preparationTime: { time: 60, unit: 'min' },
  ovenTemperature: 200,
  quote: {
    content: 'Hi, the best cookies post hangover',
    author: 'me, and chief or captain',
  },
  source: { content: "Some child's book", author: 'me' },
  imgURL: dummyLogo,
  type: RecipeType.Desserts,
  category: 'More cookies',
  language: 'en',
};

const Recipe: FC = () => {
  const { type, id, category } = useParams();
  const theme = useTheme();
  const [recipe, setRecipe] = useState<RecipeInfo>();

  useEffect(() => {
    if (type !== undefined) {
      api_recipe(type as RecipeType, category)
        .get(`/${id}`)
        .then((result) => setRecipe(result.data))
        .catch(() => {
          setRecipe(dummy);
        });
    }
    // .catch((reason) => console.error(reason));
  }, []);

  return (
    <Box width={'85%'} marginTop={4} marginBottom={4}>
      {recipe !== undefined ? (
        <>
          <Typography
            textAlign={'center'}
            variant="h2"
            color={theme.palette.primary.main}
            marginBottom={6}
          >
            {recipe.title}
          </Typography>
          <Stack direction="column">
            <Stack direction={'row'} width={'100%'} spacing={2}>
              <Box width={'50%'} display={'grid'}>
                <Box justifySelf={'flex-end'} width={'91.666667%'}>
                  <img
                    src={recipe.imgURL}
                    alt={recipe.title}
                    style={{
                      // maxWidth: '450px',
                      maxHeight: '600px',
                      // width: '100%',
                      height: 'auto',
                    }}
                  />
                </Box>
              </Box>
              <Divider orientation="vertical" flexItem />
              {recipe.quote !== undefined && (
                <Stack width={'50%'} direction={'column'}>
                  <Box sx={{ width: 30, height: 30 }}>
                    <svg
                      aria-hidden="true"
                      className="w-10 h-10 text-amber-400"
                      viewBox="0 0 24 27"
                      fill="none"
                      xmlns="http://www.w3.org/2000/svg"
                    >
                      <path
                        d="M14.017 18L14.017 10.609C14.017 4.905 17.748 1.039 23 0L23.995 2.151C21.563 3.068 20 5.789 20 8H24V18H14.017ZM0 18V10.609C0 4.905 3.748 1.038 9 0L9.996 2.151C7.563 3.068 6 5.789 6 8H9.983L9.983 18L0 18Z"
                        fill="currentColor"
                      />
                    </svg>
                  </Box>
                  <Typography variant="h6">{recipe.quote?.content}</Typography>
                  <Typography variant="h6" marginLeft={4}>
                    — {recipe.quote?.author}
                  </Typography>
                </Stack>
              )}
            </Stack>
          </Stack>
        </>
      ) : (
        <CircularProgress />
      )}
    </Box>
  );
};

export default Recipe;
